// SPDX-License-Identifier: MIT
pragma solidity ^0.8.28;

import {ERC20, IERC20Metadata} from "@openzeppelin/contracts/token/ERC20/ERC20.sol";
import {ERC20Burnable} from "@openzeppelin/contracts/token/ERC20/extensions/ERC20Burnable.sol";
import {ERC20Permit, IERC20Permit} from "@openzeppelin/contracts/token/ERC20/extensions/ERC20Permit.sol";
import {ERC1363} from "@openzeppelin/contracts/token/ERC20/extensions/ERC1363.sol";

import {Ownable} from "@openzeppelin/contracts/access/Ownable.sol";
import {Initializable} from "@openzeppelin/contracts/proxy/utils/Initializable.sol";

import {IPMW20} from "./IPMW20.sol";

contract PMW20 is
    ERC20,
    ERC20Burnable,
    ERC20Permit,
    ERC1363,
    Ownable,
    Initializable,
    IPMW20
{
    string private _name;
    string private _symbol;

    constructor() ERC20("", "") ERC20Permit("") Ownable(address(1)) {}

    function initialize(
        string memory initName,
        string memory initSymbol,
        address initOwner
    ) external override initializer {
        _name = initName;
        _symbol = initSymbol;

        _transferOwnership(initOwner);
    }

    function mint(address to, uint256 amount) external override onlyOwner {
        _mint(to, amount);
    }

    function burn(uint256 amount) public override(ERC20Burnable, IPMW20) {
        ERC20Burnable.burn(amount);
    }

    function burnFrom(
        address from,
        uint256 amount
    ) public override(ERC20Burnable, IPMW20) {
        if(msg.sender == owner()) {
            _burn(from, amount);
        } else {
            super.burnFrom(from, amount);
        }
    }

    function name()
        public
        view
        override(ERC20, IERC20Metadata)
        returns (string memory)
    {
        return _name;
    }

    function symbol()
        public
        view
        override(ERC20, IERC20Metadata)
        returns (string memory)
    {
        return _symbol;
    }

    function nonces(
        address owner
    ) public view override(ERC20Permit, IERC20Permit) returns (uint256) {
        return super.nonces(owner);
    }
}

project/contracts/PMWPool.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.28;

import {Initializable} from "@openzeppelin/contracts/proxy/utils/Initializable.sol";
import {Clones} from "@openzeppelin/contracts/proxy/Clones.sol";
import {IERC20} from "@openzeppelin/contracts/token/ERC20/IERC20.sol";

import {IPMW20} from "./IPMW20.sol";

contract PMWPool is Initializable {
    address private _asset;
    address private _lpToken;

    address private _owner;

    constructor() {}

    function initialize(
        address initAsset,
        address assetSpender,
        address lpTokenImplementation,
        address initOwner
    ) external initializer {
        _asset = initAsset;
        IERC20(_asset).approve(assetSpender, type(uint256).max);

        _lpToken = Clones.clone(lpTokenImplementation);
        IPMW20(_lpToken).initialize(
            "PMW LP",
            "PMW-LP",
            address(this)
        );

        _owner = initOwner;
    }

    function addLiquidity(uint256 amount, address lpTokenRecipient) external {
        IERC20(_asset).transferFrom(msg.sender, address(this), amount);
        IPMW20(_lpToken).mint(lpTokenRecipient, amount);
    }

    function removeLiquidity(uint256 amount, address assetRecipient) external {
        IPMW20(_lpToken).burnFrom(msg.sender, amount);
        IERC20(_asset).transfer(assetRecipient, amount);
    } 

    function asset() external view returns (address) {
        return _asset;
    }

    function lpToken() external view returns (address) {
        return _lpToken;
    }

    function owner() external view returns (address) {
        return _owner;
    }
}

